import React from 'react';
import {
  ScrollView,
  StyleSheet, Text, View, Button, Alert
} from 'react-native';
import ScreenTemplate from "../Screens/screenTemplate";
import ChatStyles from "../Screens/chatStyles";


function chatAlert() {
    return Alert.alert('Chat with a librarian', 'This button will eventually allow you to contact a librarian')
}

function emailAlert() {
    return Alert.alert('Email', 'This button will eventually allow you to email a librarian')
}

function alertFunction(comm) {
    let response
    if (comm === 'chat') {
        response = chatAlert()
    } else {
        response = emailAlert()
    }
    return response
}

export default function ChatWithALibrarian() {
  return(
    <ScreenTemplate>
      <ScrollView>
        <View
            style={{
              height: 50,
              backgroundColor:'white',
              justifyContent:'center',
              borderBottomWidth:2,
              borderColor:'black',
              borderStyle:'solid'
            }}>
            <Text>Chat with a librarian!</Text>
          </View>
        <View>
          <Button title={'Chat'} onPress={alertFunction('chat')}></Button>
          <Button title={'Email'} onPress={alertFunction('email')}></Button>
        </View>
      </ScrollView>
    </ScreenTemplate>
  )
}

