import React from 'react';
import {
  ScrollView,
  StyleSheet, Text, View, Button, Alert
} from 'react-native';
import ScreenTemplate from "../Screens/screenTemplate";
import style from "../Screens/chatStyles";

const Alerts = () => {
    const chatAlert = () =>
        Alert.alert('Alert Title', 'My Alert Msg', [
            {
                text: 'Cancel',
                onPress: () => console.log('Cancel Pressed'),
                style: 'cancel',
            },
            {text: 'OK', onPress: () => console.log('OK Pressed')},
        ]);
    const emailAlert = () =>
        Alert.alert('Alert Title', 'My Alert Msg', [
            {
                text: 'Ask me later',
                onPress: () => console.log('Ask me later pressed')
            }
        ]);
    return (
        <ScreenTemplate>
          <ScrollView>
            <View
                style={{
                  height: 50,
                  backgroundColor:'white',
                  justifyContent:'center',
                  borderBottomWidth:2,
                  borderColor:'black',
                  borderStyle:'solid'
                }}>
                <Text style={style.title}>Chat with a librarian!</Text>
              </View>
              <View style={style.buttonContainer}>
                  <View style={style.button}>
                      <Button title={'Chat'} onPress={chatAlert}></Button>
                      <Button title={'Email'} onPress={emailAlert}></Button>
                  </View>
              </View>
          </ScrollView>
        </ScreenTemplate>
    );
};


export default Alerts;
