import React from 'react';
import {
  ScrollView,
  StyleSheet, Text, View, Button, Alert
} from 'react-native';
import ScreenTemplate from "../Screens/screenTemplate";
import ChatStyles from "../Screens/chatStyles";

const Alerts = () => {
    const chatAlert = () =>
        Alert.alert('Alert Title', 'My Alert Msg', [
            {
                text: 'Cancel',
                onPress: () => console.log('Cancel Pressed'),
                style: 'cancel',
            },
            {text: 'OK', onPress: () => console.log('OK Pressed')},
        ]);
    const emailAlert = () =>
        Alert.alert('Alert Title', 'My Alert Msg', [
            {
                text: 'Ask me later',
                onPress: () => console.log('Ask me later pressed')
            }
        ]);
    return (
        <View style={ChatStyles}>
            <Button title={'chatAlert'} onPress={chatAlert} color={'blue'} />
            <Button title={'emailAlert'} onPress={emailAlert} />
        </View>
    );
};

function ChatWithALibrarian() {
  return(
    <ScreenTemplate>
      <ScrollView>
        <View
            style={{
              height: 50,
              backgroundColor:'white',
              justifyContent:'center',
              borderBottomWidth:2,
              borderColor:'black',
              borderStyle:'solid'
            }}>
            <Text>Chat with a librarian!</Text>
          </View>
        <View>
          <Button title={'Chat'} onPress={alertFunction('chat')}></Button>
          <Button title={'Email'} onPress={alertFunction('email')}></Button>
        </View>
      </ScrollView>
    </ScreenTemplate>
  )
}

export default Alerts;
